	<script>
		$(document).ready(function() {

			const questListArray = JSON.parse(questListJson());

			let html = '';
			html = html + '<ul class="list p-l-0">';
			$.each(questListArray, function(i, quest) {
				if (quest.item_reward != undefined) {
					html = html + '<li class="checkbox m-t-0"><label class="test-data">';
					html = html + `<input name="quest_id[]" id="${quest.iname}" value="${quest.iname}" type="checkbox">`;
					html = html + '<i class="input-helper"></i>';
					html = html + `<span class="quest-name">${quest.name}</span>`;
					html = html + '<span class="item-name hidden">';
					$.each(quest.item_reward, function(i, item) {
						html = html + item.name + ' ';
					});
					html = html + '</span>';
					$.each(quest.item_reward, function(i, item) {
						html = html +
							`<img style="heigth:35px; width:35px;" src="http://cdn.alchemistcodedb.com/images/items/icons/${item.url}">`;
					});
					html = html + '</label></li>';
				}

			});
			html = html + '</ul>';

			$('#mCSB_2_container').append(html);

			var monkeyList = new List('quest_id_checkbox_list', {
				valueNames: [
					'item-name',
					'quest-name',
				],
				fuzzySearch: {
					searchClass: "fuzzy-search",
					location: 0,
					distance: 200,
					threshold: 0.4,
					multiSearch: true
				}
			});

			// $('#quest_id').append(html);
			// $("#quest_id").trigger("chosen:updated");

			// $('#quest_id_target').append(html);
			// $("#quest_id_target").trigger("chosen:updated");

		});

		function clearQuestSelect() {
			$('input[name*="quest_id"]:checked').map(function() {
				this.checked = false;
			});
		}

		function doQuestNormalHard() {

			let item_current_total_done = 0;
			let round = 1;

			let quest_id = [];
			quest_id = $('input[name*="quest_id"]:checked').map(function() {
				return this.value;
			}).get();

			let current_quest = 0;
			let count_quest = quest_id.length;


			function nextCompose() {

				let url = '{post_doQuestMultiplayer}';
				let repeat_amount = $('#repeat_amount').val();

				let param = {
					'id': '{id}',
					'platform': $.cookie('platform'),
					'quest_id': quest_id[current_quest],
					'token': '{token}',
					'token_ap': '{token_ap}',
					'btlid': '{btlid}',
				};

				$.ajax({
					type: "POST",
					url: url,
					data: param,
					cache: false,
					contentType: "application/x-www-form-urlencoded",
					beforeSend: function() {
						DisButton('run_button', 'Running...');
					},
					success: function(res) {

						try {

							var res_quest = resultQuest(res);
							// ถ้าเควสผ่าน
							if (res_quest == 'success') {
								// ถ้าเควสยังไม่เท่ากับเป้าหมาย
								if (current_quest <= count_quest - 1) {
									// ถ้ารอบปัจจุบันของเควสที่ทำ น้อยกว่าที่ตั้งไว้
									round++;
									if (round <= repeat_amount) {
										// ส่งเควสเดิม ทำซ้ำรอบต่อไป
										nextCompose();
									} else {
										// ถ้าเควสปัจจุบันรอบครบแล้ว
										// เลื่อนเควสถัดไป
										current_quest++;

										if (current_quest <= count_quest - 1) {
											round = 1;
											nextCompose();
										} else {
											EnButton('run_button', 'Run');
										}

									}
								} else {

									EnButton('run_button', 'Run');
								}
							} else {
								EnButton('run_button', 'Run');
							}
						} catch (error) {
							if (res.message) {
								notify('error', res.message);
								EnButton('run_button', 'Run');
							} else {
								notify('error', 'Program Error => ' + error);
								EnButton('run_button', 'Run');
							}
						}

						function resultQuest(res) {

							try {
								if (res.body.player.selected_award == "" || res.body.player.selected_award) {

									// แสดงไอเท็มดรอป
									let questListArray = JSON.parse(questListJson());
									let html_log = '';
									html_log += `<p class="m-b-0">${round} | ${questListArray[quest_id[current_quest]].name} | Receive `;
									res.drops.forEach(items => {
										if (items.iname) {
											var name_unit_search = items.iname;
											var name_unit_search = name_unit_search.replace('UN_V2_', 'IT_PI_')

											$.each(questListArray[quest_id[current_quest]].item_reward, function(i, item) {
												if (name_unit_search == item.iname) {
													html_log = html_log +
														`<img style="heigth:35px; width:35px;" src="http://cdn.alchemistcodedb.com/images/items/icons/${item.url}">`;
													html_log = html_log + `x ${items.num}`;
												}
											});
										}
									});
									html_log = html_log + `</p>`;
									$('#text_log').prepend(html_log);
									return 'success';
								} else {
									notify('error', res.message);
									renderErrorLog(res.message);
									return 'wrong';
								}
							} catch (error) {
								if (res.message) {
									renderErrorLog(res.message);
									notify('error', res.message);
									return 'wrong';
								} else {
									renderErrorLog(error);
									notify('error', 'Program Error => ' + error);
									return 'wrong';
								}
							}

							function renderErrorLog(text) {
								$('#text_log').prepend('<h4 class="bgm-red">' + text + '</h4>');
							}

						}

					}
				});

			}

			if (quest_id == '') {
				notify('error', 'Please Select Quest');
				EnButton('run_button', 'Run');
				return false;
			} else {
				nextCompose();
			}


			// end function
		}

